graphql.query:
query entry($station: ID!) {
    entry(station: $station) {
        id
        hash
        timestamp
        temperature
        temperature2
        humidity
        feels
        createdAt
        updatedAt
        station {
            id
            name
            email
            createdAt
            updatedAt
            address {
                city
                country
                street
                zip
            }
            coordinates {
                height
                latitude
                longitude
            }
            config {
                temperature
                temperature2
            }
        }
    }
}

query entryExtreme($low: Boolean, $station: ID, $from: String, $to: String) {
